import React, {useState} from 'react';
import { StyleSheet, TouchableOpacity, Text, View, ScrollView, ImageBackground, Dimensions} from 'react-native';
import TestButton from './TestButton';
import { useForm, getErrors } from "../util/hooks";
import { useQuery, gql } from "@apollo/client";


const numColumns = 3;

function UserProfile(){   
  const [user, setUser] = useState({})
  const {data} = useQuery(FETCH_USER_QUERY, {
      onError(err){
          console.log(err);
      },
      variables: {
          userId: "5f90e4d4920bab09f6df0106"
      }
  });
  console.log(data);
  //console.log(data.username);
  //console.log(data.country);
  //console.log(data.getUser.country);
  //console.log(data.getUser.getCountry);
  //console.log(data.firstName);
  if(data && data.getUser != user){
    console.log(data.getUser.firstName);
    setUser(data.getUser);
  }

  //console.log(data.firstName);
  //console.log({data.firstName});
  console.log("Outputting to console test");
    return (
      <ScrollView>
        <ImageBackground source ={require('./images/profileBackground.png')} style= {styles.otherContainer}>
            <Text style={styles.title}>MY PROFILE</Text>
            <View style={styles.container}>
                <Text style={styles.otherInfo}>     
                    {"\n"}   
                    INSERT PROFILE PICTURE HERE{"\n\n\n"}
                    Name: {user.firstName + user.lastName + "\n"}
                    Username: { user.username + "\n" }
                    Email: {user.email + "\n" }
                    Major: {/*user.major */ "\n" }
                    Country: {/*user.country  */ "\n"}
                    Ethnicity: {/*user.ethnicity  */ "\n" }
                    Sex: {/*user.sex  */ "\n" }
                    Member since: {/*user.createdAt  */ "\n" }
                </Text>
            </View>
        </ImageBackground>
        <TestButton text='Edit Profile' />
      </ScrollView>
  )
}
const styles = StyleSheet.create({
  title:{
    textAlignVertical: 'top',
    paddingVertical: 30,
    borderColor: "#20232a",
    borderRadius: 6,
    color: "white",
    textAlign: "center",
    fontSize: 35,
    fontWeight: "bold",
    hashTag: {
      fontStyle: 'italic',
    },
  },
  otherInfo: {
    flexDirection: 'row',
    margin: 24,
    color: '#4a4c4d',
    fontSize: 23,
    fontWeight: 'bold',
    textAlign: 'left',
    lineHeight: 40,
  },
  container: {
    backgroundColor: "#b8e5ff",
    paddingVertical: 5,
    marginTop: 5,
    alignItems: 'center',
  },
  testing: {
    margin: 0,
    color: 'gray',
    fontSize: 23,
    fontWeight: 'bold',
    textAlign: 'left',
    lineHeight: 40,
    backgroundColor: '#ffffff',
  },
  testing: {
    margin: 0,
    color: 'ffffff',
    fontSize: 23,
    fontWeight: 'bold',
    textAlign: 'left',
    lineHeight: 40,
    backgroundColor: 'gray',
  },
  container2: {
    flex: 1,
    marginVertical: 20,
  },
  item: {
    backgroundColor: '#4D243D',
    alignItems: 'center',
    justifyContent: 'center',
    flex: 1,
    margin: 1,
    height: Dimensions.get('window').width / numColumns, // approximate a square
  },
  itemInvisible: {
    backgroundColor: 'transparent',
  },
  itemText: {
    color: '#fff',
  },
})

/*
container: {
    flex: 1,
    justifyContent: 'top',
    paddingTop: Constants.statusBarHeight,
    backgroundColor: '#ffffff',
    padding: 8,
  }



*/

const FETCH_USER_QUERY = gql`
  query getUser($userId: ID!) {
    getUser(userId: $userId) {
      firstName
      lastName
      photo
      username
      email
      major
      year
      graduating
      country
      ethnicity
      sex
      createdAt
      permission
      classes
      internships
      socialMedia
    }
  }
`;

export default UserProfile;
