import React, {useState} from 'react';
import { StyleSheet, TouchableOpacity, Text, View, ScrollView, ImageBackground, Dimensions, FlatList} from 'react-native';
import TestButton from './TestButton';
import { useForm, getErrors } from "../util/hooks";
import { useQuery, gql } from "@apollo/client";
import Ionicons from 'react-native-ionicons'

function UserProfile(){   
  const [user, setUser] = useState({})
  const {data} = useQuery(FETCH_USER_QUERY, {
      onError(err){
          console.log(err);
      },
      variables: {
          userId: "5f90e4d4920bab09f6df0106"
      }
  });
  console.log(data);

  if(data && data.getUser != user){
    console.log(data.getUser.firstName);
    setUser(data.getUser);
  }

  console.log("Outputting to console test");
  const dataList = [
    {key: 'Name '}, {key: user.firstName + " "+  user.lastName},
    {key: 'Username'}, {key: user.username},
    {key: 'Email'}, {key: user.email},
    {key: 'Major'}, {key: user.major},
    {key: 'Country'}, {key: user.country},
    {key: 'Ethnicity'}, {key: user.ethnicity},
    {key: 'Sex'}, {key: user.sex},
    {key: 'Member since'}, {key: user.createdAt},
  ]

  let flag=true
  let len = dataList.length;

  let _renderItem = ({item, index}) =>{
    let {itemStyle, itemText, itemStyle2, itemText2} = styles
    return(
      <View style={itemStyle}>
        <Text style={itemText}>{item.key} </Text>
      </View>
    )
  }
  const numColumns=2
    return (
      <ScrollView style= {styles.view}>
        <ImageBackground source ={require('./images/profileBackground.png')} style= {styles.container}>
          <Text style={styles.title}>MY PROFILE</Text>
        </ImageBackground>


        {/*  Add functionality to change profile picture   */}
        <View style={styles.container}>
          <TouchableOpacity style={styles.profilePic}>
              <Ionicons name="ios-add" size={40} color="#FFF" style={{marginTop: 6, marginLeft: 2}}></Ionicons>
          </TouchableOpacity>
        </View>

        {/*  Add similar u  */}
        <View style={styles.container}>
              <Text style={styles.otherInfo}>
              {"\n\n\n"}Change profile picture{"\n\n\n"}
              </Text>
        </View>


        <FlatList
          data={dataList}
          renderItem={_renderItem}
          keyExtractor={(item, index) => index.toString()}
          numColumns={numColumns}
       />
      </ScrollView>

  )
}
const styles = StyleSheet.create({
  title:{
    textAlignVertical: 'top',
    paddingVertical: 30,
    borderColor: "#20232a",
    borderRadius: 6,
    color: 'white',
    textAlign: "center",
    fontSize: 35,
    fontWeight: "bold",
  },
  itemStyle:{
    backgroundColor: '#1e1f1f',
    alignItems: 'flex-start',
    justifyContent: 'center',
    height: '100',
    flex: 1,
    marginHorizontal: 0,
    marginVertical: 0,
    paddingHorizontal:13,
  },
  itemText: {
    color: 'white',
    fontSize: 23,
    paddingHorizontal:10,
    lineHeight: 35,
    textAlign: 'right',
  },
  otherInfo: {
    flexDirection: 'row',
    color: '#0a84ff',
    fontSize: 18,
    textAlign: 'left',
    lineHeight: 10,
    justifyContent: 'space-between',
  },
  container: {
    backgroundColor: "#1e1f1f",
    alignItems: 'center',
  },
  view: {
    backgroundColor: "#1e1f1f"
  },
  profilePic:{
    width: 200,
    height: 200,
    borderRadius:100,
    backgroundColor: '#4e5252',
    marginTop: 48,
    justifyContent: 'center',
    alignItems: 'center',
  }
})

const FETCH_USER_QUERY = gql`
  query getUser($userId: ID!) {
    getUser(userId: $userId) {
      firstName
      lastName
      photo
      username
      email
      major
      year
      graduating
      country
      ethnicity
      sex
      createdAt
      permission
      classes
      internships
      socialMedia
    }
  }
`;

export default UserProfile;
