import { useForm, getErrors } from "../util/hooks";

function ViewTasks() {

    const { loading, data, error } = useQuery(FETCH_USER_QUERY, {
      variables: {
        userId: "5f90e4d4920bab09f6df0106", // dummy user for now
      },
    });
    if (loading) return null;
    if (error) return null;

      console.log("I am logging data ", data)
      const user = data.getUser;
      const allTasks = data.getTasks;
      const bookTasks = user.bookmarkedTasks;
      console.log(user); // need user for bookmarked tasks
      console.log("tatattatasks: ", data.getTasks);
      console.log("USERR TAAAAASKS: ", user.bookmarkedTasks);

