import React, { useEffect, useState } from 'react';
import { useMutation, useQuery, gql } from "@apollo/client";
import { useForm } from "react-hook-form";
import { View, TouchableWithoutFeedback, TextInput, StyleSheet, SafeAreaView, Keyboard, Button, Alert } from 'react-native';
import { KeyboardAwareScrollView } from 'react-native-keyboard-aware-scrollview';
import RNPickerSelect from 'react-native-picker-select';

import majorOptions from "../assets/options/major.json";
import yearOptions from "../assets/options/year.json";
import graduatingOptions from "../assets/options/graduating.json";
import countryOptions from "../assets/options/country.json";
import ethnicityOptions from "../assets/options/ethnicity.json";
import sexOptions from "../assets/options/sex.json";

function Register() {
    const [errors, setErrors] = useState({});

    const [addUser, { loading, error }] = useMutation(REGISTER_USER);

    const { register, handleSubmit, setValue } = useForm();

    if (error)
        console.log(error.graphQLErrors[0]);


    function onSubmit(data){
        //console.log(data);
        addUser({
            variables: {
                firstName: data.firstName,
                lastName: data.lastName,
                major: data.major,
                ethnicity: data.ethnicity,
                graduating: data.graduating,
                country: data.country,
                year: data.year,
                sex: data.sex,
                username: data.username,
                email: data.email,
                password: data.password,
                confirmPassword: data.confirmPassword,
                listServ: "false"
            }
        }).catch((err) => {return err.graphQLErrors[0].extensions.exception.errors;}).then((errors) => {
            if (errors && !errors.data){
                var errorString = "";
                
                const errorArray = Object.values(errors);

                errorArray.map(error => {
                    errorString += (error);

                    if (error != errorArray[errorArray.length - 1]){
                        errorString += "\n";
                    }
                })

                Alert.alert(errorString);
            } else {
                Alert.alert("Thank you for registering " + data.firstName + "!");
            }
        })
    }

    useEffect(() => {
        register('firstName');
        setValue('firstName', "");
        register('lastName');
        setValue('lastName', "");
        register('major');
        setValue('major', "");
        register('year');
        setValue('year', "");
        register('graduating');
        setValue('graduating', "");
        register('country');
        setValue('country', "");
        register('ethnicity');
        setValue('ethnicity', "");
        register('sex');
        setValue('sex', "");
        register('username');
        setValue('username', "");
        register('email');
        setValue('email', "");
        register('password');
        setValue('password', "");
        register('confirmPassword');
        setValue('confirmPassword', "");
    }, [register]);

    return (
        <SafeAreaView 
            style={styles.container}
        >
            <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
                <KeyboardAwareScrollView>
                    <TextInput 
                        style={styles.input}
                        placeholder='First Name'
                        onChangeText={text => {
                            setValue('firstName', text)
                        }}
                        spellCheck={false}
                        autoCorrect={false}
                    />
                    <TextInput 
                        style={styles.input}
                        placeholder='Last Name'
                        onChangeText={text => {
                            setValue('lastName', text)
                        }}
                        spellCheck={false}
                        autoCorrect={false}
                    />
                    <RNPickerSelect
                        placeholder={{
                            label: 'Major',
                            value: null,
                            color: '#9EA0A4',
                        }}
                        style={{inputIOS: styles.input}}
                        onValueChange={(value) => setValue('major', value)}
                        items={majorOptions}
                    />
                    <RNPickerSelect
                        placeholder={{
                            label: 'Year',
                            value: null,
                            color: '#9EA0A4',
                        }}
                        style={{inputIOS: styles.input}}
                        onValueChange={(value) => setValue('year', value)}
                        items={yearOptions}
                    />
                    <RNPickerSelect
                        placeholder={{
                            label: 'Graduating this year?',
                            value: null,
                            color: '#9EA0A4',
                        }}
                        style={{inputIOS: styles.input}}
                        onValueChange={(value) => setValue('graduating', value)}
                        items={graduatingOptions}
                    />
                    <RNPickerSelect
                        placeholder={{
                            label: 'Country of Origin',
                            value: null,
                            color: '#9EA0A4',
                        }}
                        style={{inputIOS: styles.input}}
                        onValueChange={(value) => setValue('country', value)}
                        items={countryOptions}
                    />
                    <RNPickerSelect
                        placeholder={{
                            label: 'Ethnicity',
                            value: null,
                            color: '#9EA0A4',
                        }}
                        style={{inputIOS: styles.input}}
                        onValueChange={(value) => setValue('ethnicity', value)}
                        items={ethnicityOptions}
                    />
                    <RNPickerSelect
                        placeholder={{
                            label: 'Sex',
                            value: null,
                            color: '#9EA0A4',
                        }}
                        style={{inputIOS: styles.input}}
                        onValueChange={(value) => setValue('sex', value)}
                        items={sexOptions}
                    />
                    <TextInput 
                        style={styles.input}
                        placeholder='Username'
                        onChangeText={text => {
                            setValue('username', text)
                        }}
                        spellCheck={false}
                        autoCorrect={false}
                        autoCapitalize='none'
                    />
                    <TextInput 
                        style={styles.input}
                        placeholder='UF/SF Email'
                        onChangeText={text => {
                            setValue('email', text)
                        }}
                        spellCheck={false}
                        autoCorrect={false}
                        autoCapitalize='none'
                    />
                    <TextInput 
                        style={styles.input}
                        placeholder='Password'
                        onChangeText={text => {
                            setValue('password', text)
                        }}
                        spellCheck={false}
                        autoCorrect={false}
                        secureTextEntry={true}
                        autoCapitalize='none'
                    />
                    <TextInput 
                        style={styles.input}
                        placeholder='Confirm Password'
                        onChangeText={text => {
                            setValue('confirmPassword', text)
                        }}
                        spellCheck={false}
                        autoCorrect={false}
                        secureTextEntry={true}
                        autoCapitalize='none'
                    />

                    <View>
                        <Button 
                            title='Submit'
                            onPress={handleSubmit(onSubmit)}
                        />
                    </View>
                    <View style={{ flex : 1 }} />
                </KeyboardAwareScrollView>
            </TouchableWithoutFeedback>
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    input: {
        width: 350,
        height: 55,
        backgroundColor: 'white',
        margin: 10,
        padding: 8,
        color: 'black',
        borderRadius: 14,
        fontSize: 18,
        fontWeight: '500',
      },
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#42A5F5',
      }
  });

const pickerStyles = StyleSheet.create({
    inputIOS: {
        width: 350,
        height: 55,
        backgroundColor: 'white',
        margin: 10,
        padding: 8,
        color: 'black',
        borderRadius: 14,
        fontSize: 18,
        fontWeight: '500',
    },
    inputAndroid: {
        color: 'white'
    }
});

const REGISTER_USER = gql`
  mutation register(
    $firstName: String!
    $lastName: String!
    $major: String!
    $year: String!
    $graduating: String!
    $country: String!
    $ethnicity: String!
    $sex: String!
    $username: String!
    $email: String!
    $password: String!
    $confirmPassword: String!
    $listServ: String!
  ) {
    register(
      registerInput: {
        firstName: $firstName
        lastName: $lastName
        major: $major
        year: $year
        graduating: $graduating
        country: $country
        ethnicity: $ethnicity
        sex: $sex
        username: $username
        email: $email
        password: $password
        confirmPassword: $confirmPassword
        listServ: $listServ
      }
    ) {
      id
      email
      username
      createdAt
    }
  }
`;

export default Register;
