import React, { useState } from "react";
import { useForm } from "react-hook-form";
import {
  Alert,
  Modal,
  StyleSheet,
  Text,
  TouchableHighlight,
  View,
  TextInput,
} from "react-native";
import { useMutation, useQuery } from "@apollo/client";
import gql from "graphql-tag";

function Button() {
  let user = {
    username: "alambrito",
    firstName: "Alan",
    lastName: "Brito",
    points: 420,
    fallPoints: 69,
  };

  let { data, refetch } = useQuery(FETCH_USER_QUERY, {
    variables: {
      userId: id,
    },
  });

  if (data) {
    let actualUsers = data.getUser;
  }

  console.log(actualUser);
  const { register, handleSubmit, setValue } = useForm();

  const [redeemPointsModal, setRedeemPointsModal] = useState(false);

  const openModal = (name) => {
    if (name === "redeemPoints") {
      setRedeemPointsModal(true);
    }
  };

  const closeModal = (name) => {
    if (name === "redeemPoints") {
      setRedeemPointsModal(false);
    }
  };

  const onSubmit = () => {
    setRedeemPointsModal(false);
  };
  const [value, onChangeText] = React.useState("Enter a code here...");

  return (
    <View style={styles.centeredView}>
      <Modal
        animationType="slide"
        transparent={true}
        visible={redeemPointsModal}
        onRequestClose={() => {
          Alert.alert("Modal has been closed.");
        }}
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <Text style={styles.modalText}>Event code:</Text>
            <Text style={styles.modalText}>{user.fallPoints}</Text>
            <TextInput
              style={styles.input}
              placeholder="..."
              onChangeText={(text) => {
                setValue("firstName", text);
              }}
              spellCheck={false}
              autoCorrect={false}
            />

            <TouchableHighlight
              style={{ ...styles.openButton, backgroundColor: "#004D73" }}
              onPress={handleSubmit(onSubmit)}
            >
              <Text style={styles.textStyle}>Submit</Text>
            </TouchableHighlight>

            <TouchableHighlight
              style={{ ...styles.openButton, backgroundColor: "gray" }}
              onPress={() => closeModal("redeemPoints")}
            >
              <Text style={styles.textStyle}>Cancel</Text>
            </TouchableHighlight>
          </View>
        </View>
      </Modal>

      <Text style={{ ...styles.textStyle, color: "black" }}>
        This is what you submitted last time:
      </Text>
      <Text style={{ ...styles.textStyle, color: "black" }}>
        {user.fallPoints}
      </Text>
      <TouchableHighlight
        style={styles.openButton}
        onPress={() => openModal("redeemPoints")}
      >
        <Text style={styles.textStyle}>Redeem Code</Text>
      </TouchableHighlight>
    </View>
  );
}

const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22,
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  openButton: {
    backgroundColor: "#004D73",
    borderRadius: 20,
    padding: 10,
    elevation: 2,
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center",
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center",
  },
});

const FETCH_USER_QUERY = gql`
  query getUser($userId: ID!) {
    getUser(userId: $userId) {
      firstName
      lastName
      points
      fallPoints
      springPoints
      summerPoints
      fallPercentile
      springPercentile
      summerPercentile
      events {
        name
        category
        createdAt
        points
      }
      tasks {
        name
        points
        startDate
      }
      bookmarkedTasks
    }
  }
`;

const REDEEM_POINTS_MUTATION = gql`
  mutation redeemPoints($code: String!, $username: String!) {
    redeemPoints(redeemPointsInput: { code: $code, username: $username }) {
      points
      fallPoints
      springPoints
      summerPoints
      message
      events {
        id
        name
        category
        createdAt
        points
      }
      tasks {
        name
        points
        createdAt
      }
    }
  }
`;

export default Button;
